mapper.insert:
INSERT INTO
	member
	(
	memType
	, nickname
	, email
	, pwd
	, gender
	, dob
	, location
	, memDesc
	)VALUES(
		#{memType}
		, #{nickname}
		, #{email}
		, #{pwd}
		, #{gender}
		, #{dob}
		, #{location}
		, #{memDesc}
	)

mapper.update:
UPDATE member
SET
	pwd = #{pwd}
	, location = #{location}
	, memDesc = #{memDesc}
WHERE 1=1
	AND seq = #{seq}

UPDATE member
SET 
	memType = #{memType}
WHERE 1=1
	AND seq = #{seq}
