mapper.sql:
   		FROM party AS p
		WHERE 1=1
		AND delNy = 0
		AND partySport = #{partySport}
		AND partyName LIKE CONCAT('%',#{partyName},'%')
		AND partyGen = #{partyGen}
		AND partyAge = #{partyAge}
		AND playDt = #{playDt}

mapper.update:
UPDATE party
SET
	partyName = #{partyName}
	, playerMax = #{playerMax}

WHERE
	seq = #{seq}

mapper.insert:
    	INSERT INTO party (
    		partyType
    		, partyName
    		, playerNum
    		, playerMax
    		, playDt
    		, playTimeStart
			, playTimeEnd
    		, partyGen
    		, toolNy
    		, delNy
    	) VALUES (
    		"0"
    		, #{partyName}
    		, "1"
    		, #{playerMax}
    		, #{playDt}
    		, #{playTimeStart}
    		, #{playTimeEnd}
    		, #{partyGen}
    		, #{toolNy}
    		, "0"
    	)
