mapper.sql:
   		FROM party AS p
		WHERE 1=1
		AND delNy = 0
			AND partySport = #{partySport}
			AND partyName LIKE CONCAT('%',#{partyName},'%')
			AND partyGen = #{partyGen}
			AND partyAge = #{partyAge}
			AND playDt = #{playDt}

mapper.update:
UPDATE party
SET
	partyName = #{partyName}
	, playerMax = #{playerMax}

WHERE
	seq = #{seq}

